INSERT INTO `comic` (`bookID`, `bookname`, `author`, `bookimg`, `label`, `readnum`, `subscribe`, `reward`, `yuepiao`, `tuijian`, `Time`, `score`, `comment`, `miaoshu`, `index`, `newset`, `desc`) VALUES
	(1, '兽宠女皇', '豆豆龙动漫', 'https://image.zymkcdn.com/file/cover/000/003/654.jpg-300x400.webp', '玄幻、后宫、穿越', 326, 26070, 751, 3036, 10310, '每周一、五更新', 9.6, 1702, '独家每周一五更新夜店歌手卓菡被一名兽耳男子找到，说她是兽族女皇，并让她返回兽界继承皇位，而且还要挑夫婿生孩子！穿越到兽界，不想沦为生育工具的卓菡奋起反抗，但是她与生俱来的魅香却总是令她陷入被异性强行求爱的危机之中。卓菡反对直接交配的兽族制度，提出先恋爱再结婚。于是，卓菡与来自狼族犬族狐族虎族兔族的四名骑士周旋……当她日益习惯这里时，失踪的姐姐卓樱回到皇宫，原来她只是个替代品……', 0, '第78话，暗黑龙王出现', '兽爱是真情还是发情？'),
	(2, '灵剑尊', 'iCiyuan动漫', 'https://image.zymkcdn.com/file/cover/000/001/417.jpg-300x400.webp', '热血、古风、武侠', 4818, 18, 51940, 34376, 11, '每周一、五更新', 9.7, 20946, '每周一五更新强者遇袭，重返少年时代，成为当年弱小的废物少主。前世的仇人，今生，绝不会放过！前世的遗憾，今生，一定要弥补！待到灵剑长啸之时，天地三界，我为至尊！若有不从者，一剑，杀之！粉丝群424157105（已满，少有位置）粉丝群471649635(火爆)', 0, '245话，虫草同命蛊 ', '天地三界我为至尊'),
	(3, '至尊神级系统', '二次元动漫', 'https://image.zymkcdn.com/file/cover/000/003/695.jpg-300x400.webp', '玄幻、修真、穿越', 979, 52813, 2989, 6987, 24463, '每周一、三、五', 9.8, 1305, '每周一三五六七更新地球少年，穿越异世，凭借新闻系统，知天下万事，占夺宝先机修绝世功法，练逆天武技吃极品丹药，掌天地神兵！最终踏上众生之巅，成就至尊之位。', 0, '171话，妖族 ', '知天下万事，占夺宝先'),
	(4, '哦，我的宠妃大人', '极漫文化 爱涂星际', 'https://image.zymkcdn.com/file/cover/000/002/957.jpg-300x400.webp', '恋爱、穿越、古风', 1141, 80069, 4029, 8880, 36107, '每周一、日', 9.8, 3139, '独家每周一日更新正统戏精的豪放小妾x衣冠禽兽的宠妻狂魔，古典浪漫花式甜宠看过来！', 0, '130话，一家人不说两家话 ', '衣冠禽兽的宠妻狂魔'),
	(5, '我把天道修歪了', '咸鱼纲', 'https://image.zymkcdn.com/file/cover/000/003/248.jpg-300x400.webp', '恋爱、武侠、修真', 310, 30764, 2350, 3408, 10205, '每周五、六更新', 9.5, 1682, '独家每周五六更新天意如刀，斩万物寿元。镇天宫以大阵镇苍天，为天下苍生与天争命。可逆天改命却受到诅咒，五百年来人才凋零。直到镇天殿天池上的一株墨莲化形而出……', 0, '63话，白雪之死 ', '双魂少年逆天改命'),
	(6, '伏魔天师', '二次元动漫 图灵动漫', 'https://image.zymkcdn.com/file/cover/000/001/315.jpg-300x400.webp', '玄幻、悬疑、热血', 3867, 14, 64999, 17269, 53494, '每周一、五更新', 9.4, 10482, '123独家每周一五更新钟馗和他的徒弟秦霜通过一次次的降妖除魔渐渐发现这些妖物的古怪联系和他们的一些奇怪布置，最后查出有人想在京城布置这样的法阵。', 0, '205话，', '双魂少年逆天改命'),
